mapping-configuration.procedure:
CREATE PROC SET_OPERATION_AUTH_STATE_PROC @AUPE_ID VARCHAR(36), @BUTTON_IDS VARCHAR(max), @REF_MAIN_PAGE_ID VARCHAR(36), @REF_PAGE_ID VARCHAR(36), @REF_LAYOUT_ID VARCHAR(36), @MENU_CODE VARCHAR(36), @AUTH_FLAG INT, @CREATE_USER_ID CHAR(36), @CREATE_DATE DATETIME, @PROJECT_CODE VARCHAR(50), @TENANT_ID CHAR(36), @ALLOW_RESULT INT OUTPUT, @MESSAGE VARCHAR(50) OUTPUT, @MESSAGE_TYPE VARCHAR(20) OUTPUT AS /*根据权限主表ID，查询关联的是角色还是人员*/ DECLARE @REF_OBJ_ID VARCHAR(36) DECLARE @REF_OBJ_TYPE VARCHAR(10) DECLARE @AUPAST_WEIGHT INT DECLARE @LAYOUT_ID VARCHAR(36) DECLARE @AUTH_ALLOW_FLAG INT DECLARE @AUTH_NOT_ALLOW_FLAG INT DECLARE @MENU_ID VARCHAR(36)
SET @MENU_ID = (SELECT ID FROM dbo.SMT_MENU WHERE MENU_CODE = @MENU_CODE) /*根据传入IDS的类型筛选操作的ID*/ DECLARE @TOOLBAR_IDS_TABLE TABLE ( ID varchar(100), COMPONENT_ID VARCHAR(50) )
INSERT INTO @TOOLBAR_IDS_TABLE( ID, COMPONENT_ID ) SELECT A.value,S.COMPONENT_ID FROM ( select DISTINCT value from dbo.F_SPLIT(@BUTTON_IDS,',') ) A LEFT JOIN SMT_SETTING_LAYOUT_STRUCTURE S ON A.value = S.REF_ID and S.PAGE_ID = @REF_LAYOUT_ID WHERE S.COMPONENT_ATTR_TYPE IN ('rowActions','toolbar')
SET @REF_OBJ_ID = (SELECT AUPE_REF_OBJ_ID FROM SmartOne2_VC.dbo.SMT_AUTH_PERMISSION WHERE ID=@AUPE_ID) IF EXISTS (SELECT TOP 1 1 FROM SMT_BASE.dbo.BASE_USER WHERE ID = @REF_OBJ_ID) BEGIN SET @REF_OBJ_TYPE = 'user' SET @AUPAST_WEIGHT = 3 END ELSE IF EXISTS (SELECT TOP 1 1 FROM SMT_BASE.dbo.BASE_ROLE WHERE CODE = @REF_OBJ_ID) BEGIN SET @REF_OBJ_TYPE = 'role' SET @AUPAST_WEIGHT = 1 END
/*设置授权的标识*/ IF(@AUTH_FLAG = 1) BEGIN SET @AUTH_ALLOW_FLAG = 1 SET @AUTH_NOT_ALLOW_FLAG = 0 END ELSE IF (@AUTH_FLAG = 2) BEGIN SET @AUTH_ALLOW_FLAG = 0 SET @AUTH_NOT_ALLOW_FLAG = 2 END ELSE IF(@AUTH_FLAG = 0) BEGIN SET @AUTH_ALLOW_FLAG = 0 SET @AUTH_NOT_ALLOW_FLAG = 0 END
/*将表变量的ID进行分类：1.之前和现在都有的不处理；2.之前有现在没有的，将授权值更新为0；3.之前没有现在有的，插入新纪录，允许权限字段更新为1*/ /*3.插入新纪录，允许权限字段更新为1，先在数据权限表里加上，之后在授权表中加入一条数据*/ INSERT INTO SmartOne2_VC.dbo.SMT_AUTH_PAGE_STRUCT ( AUPAST_AUTH_ALLOW , AUPAST_TYPE , AUPAST_ENABLE_PERMISSION , AUPAST_REF_OBJ_ID , AUPAST_WEIGHT , AUPAST_AUTH_TYPE , AUPAST_AUTH_NOT_ALLOW , AUPAST_STATE , ID , CREATE_USER_ID , CREATE_DATE , PROJECT_CODE , TENANT_ID , AUPE_ID , AUPAST_MENU_ID , AUPAST_PAGE_ID , AUPAST_MAIN_PAGE_ID , AUPAST_REF_COMP_ID ) SELECT @AUTH_ALLOW_FLAG, 'operate', 1, ID, @AUPAST_WEIGHT, @REF_OBJ_TYPE, @AUTH_NOT_ALLOW_FLAG, 1, NEWID(), @CREATE_USER_ID, @CREATE_DATE, @PROJECT_CODE, @TENANT_ID, @AUPE_ID, @MENU_ID, @REF_PAGE_ID, @REF_MAIN_PAGE_ID, COMPONENT_ID FROM @TOOLBAR_IDS_TABLE WHERE ID NOT IN( SELECT AUPAST_REF_OBJ_ID FROM SmartOne2_VC.dbo.SMT_AUTH_PAGE_STRUCT WHERE AUPAST_PAGE_ID = @REF_PAGE_ID AND AUPAST_MENU_ID = @MENU_ID AND AUPE_ID = @AUPE_ID ) /*2. 授权值 */ UPDATE SmartOne2_VC.dbo.SMT_AUTH_PAGE_STRUCT SET AUPAST_AUTH_ALLOW = @AUTH_ALLOW_FLAG WHERE AUPAST_PAGE_ID = @REF_PAGE_ID AND AUPAST_MENU_ID = @MENU_ID AND AUPE_ID = @AUPE_ID /*AND AUPAST_REF_OBJ_ID IN (SELECT AUPAST_REF_OBJ_ID FROM dbo.SYS_AUTH_PAGE_STRUCT)*/ AND SmartOne2_VC.dbo.SMT_AUTH_PAGE_STRUCT.AUPAST_REF_OBJ_ID IN (SELECT ID FROM @TOOLBAR_IDS_TABLE)
/*对表中已经有的数据进行更新*/ UPDATE SmartOne2_VC.dbo.SMT_AUTH_PAGE_STRUCT SET AUPAST_AUTH_ALLOW = @AUTH_ALLOW_FLAG, AUPAST_AUTH_NOT_ALLOW = @AUTH_NOT_ALLOW_FLAG WHERE AUPAST_PAGE_ID = @REF_PAGE_ID AND AUPAST_MENU_ID = @MENU_ID AND AUPE_ID = @AUPE_ID /*AND AUPAST_REF_OBJ_ID IN (SELECT AUPAST_REF_OBJ_ID FROM dbo.SYS_AUTH_PAGE_STRUCT)*/ AND SmartOne2_VC.dbo.SMT_AUTH_PAGE_STRUCT.AUPAST_REF_OBJ_ID IN (SELECT ID FROM @TOOLBAR_IDS_TABLE) SET @MESSAGE = '授权设置完成' SET @MESSAGE_TYPE = 'success' SET @ALLOW_RESULT = @AUTH_FLAG
